mapper.select:
SELECT
    user.id,
    user.nick_name,
    user.phone_number,
    user.password,
    user.email
FROM user
WHERE
    1 = 1

        AND user.id = #{id}


        AND user.phone_number = #{phoneNumber}


        AND user.nick_name = #{nickName}


        AND user.password = #{password}


        AND user.email = #{email}
